import React, { useState } from 'react';
import './Crud.css';

export default function App() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [name, setName] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  const handleAddUser = () => {
    if (name.trim() === '') return;

    if (editingIndex !== null) {
      const updatedUsers = [...users];
      updatedUsers[editingIndex] = name;
      setUsers(updatedUsers);
      setEditingIndex(null);
    } else {
      setUsers([...users, name]);
    }

    setName('');
  };

  const handleEdit = (index) => {
    setName(users[index]);
    setEditingIndex(index);
  };

  const handleDelete = (index) => {
    const updatedUsers = users.filter((_, i) => i !== index);
    setUsers(updatedUsers);
  };

  const filteredUsers = users.filter(user =>
    user.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container">
      <h2>User CRUD App</h2>

      <input
        type="text"
        placeholder="Search user..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="input"
      />

      <div className="form">
        <input
          type="text"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="input"
        />
        <button onClick={handleAddUser} className="btn">
          {editingIndex !== null ? 'Update' : 'Add'}
        </button>
      </div>

      <ul className="user-list">
        {filteredUsers.map((user, index) => (
          <li key={index} className="user-item">
            <span>{user}</span>
            <div>
              <button onClick={() => handleEdit(index)} className="btn edit">Edit</button>
              <button onClick={() => handleDelete(index)} className="btn delete">Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
